import React from "react";
// import './style.scss';
import style from "./Lista.module.scss";

function Lista(){ //paramentros da função e sem funçao obg, apenas retornar oqe escrever
    const tarefas = [{
        tarefa: 'React',
        tempo: '02:00:00'
    },{
        tarefa: 'JavaScript',
        tempo:  '01:00:00'

},{
        tarefa: 'TypeScript',
        tempo: '03:00:00'
}]
    return(
         <aside className={lista.listaTarefas}>
            <h2>
                Estudo do Dia
                <ul>
                    {tarefas.map((item, index) => (//parentes para retornar, chave para ter um escopo
                        <li key={index} className="item"> {/*key pra identificar pois nao tem um class*/}
                            <h3>{item.tarefa}</h3>
                            <span>{item.tempo}</span>
                        </li>
                    ))} 

                </ul>
            </h2>

         </aside>
    )
}

export default Lista;
